<?phpif(preg_match('#' . basename(__FILE__) . '#', $_SERVER['PHP_SELF'])) { 	die('You are not allowed to call this page directly.'); }/* Edição de resumos */function ic_deletar_certificado($minicurso, $quem){	global $wpdb;	$inscritos = split("\|",$minicurso[0]->minicurso_certificado);	if(count($inscritos) == 1 AND $inscritos[0] == $quem){ $certificadoNew = NULL; }	else { 		if ($inscritos[0] == $quem) {				$nome = $quem."|";				$certificadoNew = str_replace($nome, '', $minicurso[0]->minicurso_certificado);		}		else {			$nome = "|".$quem;			$certificadoNew = str_replace($nome, '', $minicurso[0]->minicurso_certificado);		}		}	$atualizaCertificados = "update wp_minicursos set minicurso_certificado='".$certificadoNew."' where minicurso_id ='".$minicurso[0]->minicurso_id."'";	$wpdb->query($atualizaCertificados);}?>