<?phpif(preg_match('#' . basename(__FILE__) . '#', $_SERVER['PHP_SELF'])) { 	die('You are not allowed to call this page directly.'); }/* Edição de resumos */function ic_deletar_inscrito($quem){	global $wpdb;		$recebeMinicursos = split("\|",$quem[0]->minicursos);		foreach($recebeMinicursos as $recebeMini) {			$sqlMini="select * from wp_minicursos where minicurso_id='".$recebeMini."'";			$inscricaoMinicursos = $wpdb->get_results($sqlMini);			foreach($inscricaoMinicursos as $inscrever) {				$inscritos = split("\|",$inscrever->minicurso_inscritos);										if(count($inscritos) == 1 AND $inscritos[0] == $quem[0]->user_email){ $inscricaoDel = NULL; }				else { 										if ($inscritos[0] == $quem[0]->user_email) {						$nome = $quem[0]->user_email."|";						$inscricaoDel = str_replace($nome, '', $inscrever->minicurso_inscritos);					}					else {						$nome = "|".$quem[0]->user_email;						$inscricaoDel = str_replace($nome, '', $inscrever->minicurso_inscritos);					}								}				$certificados = split("\|",$inscrever->minicurso_certificado);				if(count($certificados) == 1 AND $certificados[0] == $quem[0]->user_email){ $certificadoDel = NULL; }				else { 										if ($certificados[0] == $quem[0]->user_email) {						$nomeDel = $quem[0]->user_email."|";						$certificadoDel = str_replace($nomeDel, '', $inscrever->minicurso_certificado);					}					else {						$nomeDel = "|".$quem[0]->user_email;						$certificadoDel = str_replace($nomeDel, '', $inscrever->minicurso_certificado);					}								}								$atualizaIncritos = "update wp_minicursos set minicurso_inscritos=\"".$inscricaoDel."\", minicurso_certificado=\"".$certificadoDel."\" where minicurso_id ='".$inscrever->minicurso_id."' ";				$wpdb->query($atualizaIncritos);			}		}			$wpdb->query( "DELETE FROM wp_resumos where ID='".$quem[0]->inscricao_id."'" );	$wpdb->query( "DELETE FROM wp_users where ID='".$quem[0]->ID."'" );	$wpdb->query( "DELETE FROM wp_usermeta where user_id='".$quem[0]->ID."'" );}function ic_deletar_inscrito_cert($quem){	global $wpdb;	$sql = "update wp_users set certificados='' where ID='".$quem."'"; 	$wpdb->query( $sql );}?>