<?phpif(preg_match('#' . basename(__FILE__) . '#', $_SERVER['PHP_SELF'])) { 	die('You are not allowed to call this page directly.'); }/* Formulário para Inscrito */function template_inscrito($idForm,$inscritos){	global $current_user;	global $wpdb;	if($idForm == null) { $idForm = $inscritos[0]->inscricao_id; }	$content='		<div class="form-inscrito">			<div id="container-form">				<form id="resumo" name="resumo" method="post" action="?page=ic-inscrito&action=update_inscrito">					<input type="hidden" name="ID" id="ID" value="'.$inscritos[0]->ID.'">										<input type="hidden" name="action" id="action" value="update_inscrito">										<input type="hidden" name="inscricao-id" id="inscricao-id" value="'.$idForm.'">										<input type="hidden" name="minicursos-old" id="minicursos-old" value="'.$inscritos[0]->minicursos.'">										<p><strong>Nome completo:</strong><br> 						<input name="nome-completo" type="text" id="nome-completo" width="421" style="width: 421px;" maxlength="250" value="'.@$inscritos[0]->display_name.'">					</p>							<p><strong>Instituição de ensino:</strong><br> 					<input type="radio" name="instituicao-ensino" id="instituicao-ensino" value="UFF" onChange="ver_opcao_instituicao(this.value)" '; 					if($inscritos[0]->instituicao_ensino=="UFF") $content.= 'checked'; 					$content.='> UFF 					<input type="radio" name="instituicao-ensino" id="instituicao-ensino" value="IFF" onChange="ver_opcao_instituicao(this.value)" '; 					if($inscritos[0]->instituicao_ensino=="IFF") $content.= 'checked'; 					$content.='> IFF 					<input type="radio" name="instituicao-ensino" id="instituicao-ensino" value="UENF" onChange="ver_opcao_instituicao(this.value)" ';					if($inscritos[0]->instituicao_ensino=="UENF") $content.= 'checked'; 					$content.='> UENF 					<input type="radio" name="instituicao-ensino" id="instituicao-ensino" value="OUTRAS" onChange="ver_opcao_instituicao(this.value)" '; 					if($inscritos[0]->instituicao_ensino!="UFF" and $inscritos[0]->instituicao_ensino!="IFF" and $inscritos[0]->instituicao_ensino!="UENF") $content.= 'checked'; 					$content.='> OUTRAS <br>					<input name="instituicao-ensino-outros" type="text" id="instituicao-ensino-outros" value="'.$inscritos[0]->instituicao_ensino.'" width="421" style="width: 421px;" maxlength="255" disabled>										<div class="ver_iff" id="ver_iff">						<input type="radio" name="instituicao-divisao" value="Campus Cabo Frio"';						if($inscritos[0]->instituicao_divisao=="Campus Cabo Frio") $content.=' checked ';						$content.='> Campus Cabo Frio &nbsp; &nbsp; 												<input type="radio" name="instituicao-divisao" value="Campus Campos"';						if($inscritos[0]->instituicao_divisao=="Campus Campos") $content.=' checked ';						$content.='> Campus Campos &nbsp; &nbsp; 						<input type="radio" name="instituicao-divisao" value="Campus Macaé"';						if($inscritos[0]->instituicao_divisao=="Campus Macaé") $content.=' checked ';						$content.='> Campus Macaé &nbsp; &nbsp; 						<input type="radio" name="instituicao-divisao" value="Campus Itaperuna"';						if($inscritos[0]->instituicao_divisao=="Campus Itaperuna") $content.=' checked ';						$content.='> Campus Itaperuna &nbsp; &nbsp; 										<input type="radio" name="instituicao-divisao" value="Campus Guarus"';						if($inscritos[0]->instituicao_divisao=="Campus Guarus") $content.=' checked ';						$content.='> Campus Guarus &nbsp; &nbsp; 										<input type="radio" name="instituicao-divisao" value="Campus Bom Jesus"';						if($inscritos[0]->instituicao_divisao=="Campus Bom Jesus") $content.=' checked ';						$content.='> Campus Bom Jesus 					</div>					<div class="ver_uenf" id="ver_uenf">						<input type="radio" name="instituicao-divisao" value="Bolsa Pibic-UENF"';						if($inscritos[0]->instituicao_divisao=="Bolsa Pibic-UENF") $content.=' checked ';						$content.='> Bolsa Pibic-UENF &nbsp; &nbsp; 						<input type="radio" name="instituicao-divisao" value="Bolsa IC-Outros"';						if($inscritos[0]->instituicao_divisao=="Bolsa IC-Outros") $content.=' checked ';						$content.='> Bolsa IC-Outros					</div>					</p>										';										$content.='					<p><strong>Vínculo com a Instituição:</strong><br>					<input type="radio" name="instituicao-vinculo" id="instituicao-vinculo" value="Aluno"					';					if($inscritos[0]->instituicao_vinculo=="Aluno") $content.=' checked ';					$content.='>Aluno &nbsp;					<input type="radio" name="instituicao-vinculo" id="instituicao-vinculo" value="Professor"					';					if($inscritos[0]->instituicao_vinculo=="Professor") $content.=' checked ';					$content.='>Professor &nbsp;					<input type="radio" name="instituicao-vinculo" id="instituicao-vinculo" value="Funcionário"					';					if($inscritos[0]->instituicao_vinculo=="Funcionário") $content.=' checked ';					$content.='>Funcionário &nbsp;					<input type="radio" name="instituicao-vinculo" id="instituicao-vinculo" value="Pesquisador"					';					if($inscritos[0]->instituicao_vinculo=="Pesquisador") $content.=' checked ';					$content.='>Pesquisador &nbsp;					<input type="radio" name="instituicao-vinculo" id="instituicao-vinculo" value="Outros"					';					if($inscritos[0]->instituicao_vinculo=="Outros") $content.=' checked ';					$content.='>Outros										</p>										<p><strong>E-mail:</strong><br>					<input name="email" type="text" id="email" width="421" style="width: 421px;" maxlength="255" value="'.@$inscritos[0]->user_email.'" readonly>					</p>										<p><strong>Senha:</strong><br>					<input name="senha" type="text" id="senha" width="421" style="width: 421px;" maxlength="255" value="">					</p>						<p><strong>Certificado de Congressista:</strong><br>					<input type="radio" name="certificados" value="SIM"										';									if(@$inscritos[0]->certificados == "SIM") { $content.=' checked'; } 					$content.="> SIM &nbsp; &nbsp; 					<input type=\"radio\" name=\"certificados\" value=\"\"									";					if(@$inscritos[0]->certificados == null) { $content.=' checked'; } 					$content.='> NÃO &nbsp; &nbsp;					</p>															<p><strong>Minicursos</strong><br>										';					$minicursos = $wpdb->get_results( "SELECT * FROM wp_minicursos" );					foreach($minicursos as $mini){						if($mini->minicurso_inscritos != null) {							$recebeInscritos = split("\|",$mini->minicurso_inscritos);							$recebeInscritos = ($mini->minicurso_vagas - count($recebeInscritos));						}else{							$recebeInscritos = $mini->minicurso_vagas;						}						$content.='							<div class="minicurso_'.$mini->minicurso_id.'" id="minicurso_'.$mini->minicurso_id.'">							<p>								<input type="checkbox" name="minicurso[]" id="'.$mini->minicurso_id.'" value="'.$mini->minicurso_id.'" onClick="esquema_minicurso(this.value)"							';								$recebeMinicursos = split("\|",$inscritos[0]->minicursos);								foreach($recebeMinicursos as $recebeMini) {									if($recebeMini == $mini->minicurso_id) { $content.='checked';}								}																	$content.='> 																<strong>MINICURSO '.$mini->minicurso_id.'</strong><br>								<strong>Tema: </strong>'.$mini->minicurso_tema.'<br>								<strong>Professor(es): </strong>'.$mini->minicurso_professor.'<br>								<strong>Data: </strong>'.$mini->minicurso_data.'<br>								<strong>Vagas: </strong>'.$recebeInscritos.' de '.$mini->minicurso_vagas.'<br>									<strong>Carga horária: </strong>'.$mini->minicurso_carga.'<br>								<strong>Horário: </strong>'.$mini->minicurso_horario.'<br>												</p>							</div>						';					}					$content.='					<p></p>					<p style="text-align:left;"><input type="submit" name="enviar" id="enviar" value="Enviar" /></p>				</form>';					$content.='				<script language="javascript">					jQuery(document).ready(function(){';										if(@$inscritos[0]->instituicao_ensino){						if($inscritos[0]->instituicao_ensino=='IFF') $content.='jQuery("#ver_iff").show("slow"); document.getElementById("instituicao-ensino-outros").value="";';						if($inscritos[0]->instituicao_ensino=='UENF') $content.='jQuery("#ver_uenf").show("slow"); document.getElementById("instituicao-ensino-outros").value="";';						if($inscritos[0]->instituicao_ensino!="UFF" and $inscritos[0]->instituicao_ensino!="IFF" and $inscritos[0]->instituicao_ensino!="UENF") $content.='document.getElementById("instituicao-ensino-outros").disabled = false;';					}										$content.='	}); 							</script>';								echo $content;}?>