<?phpif(preg_match('#' . basename(__FILE__) . '#', $_SERVER['PHP_SELF'])) { 	die('You are not allowed to call this page directly.'); }/* Edição de resumos */function ic_senha_inscrito($quem){	global $wpdb;       	$aux=rand( 000000  , 999999  );        $senha = md5($aux);		$atualizaSenha = "update wp_users set user_pass='".$senha."' where ID='".$quem[0]->ID."'"; 		$wpdb->query($atualizaSenha);			//ENVIA EMAIL		$to = $quem[0]->user_email;		$from = "PIBIC < PIBIC>";		$msg = "Sua inscrição foi efetuada com sucesso ".$quem[0]->display_name."\n\n";		$msg .= "Seu login é: ".$quem[0]->user_email."\n\n";		$msg .= "Sua senha é: ".$aux."\n\n";		$msg .="Para enviar seu resumo, é necessário você você faça login no site do evento: http://www.uenf.br/eventos/confict/login";		$sub="Inscrição CONFICT";		$hdrs = "From: CONFICT <confict@uenf.br>\r\n\\";		@wp_mail($to,$sub, $msg, $hdrs);}?>