<?phpif(preg_match('#' . basename(__FILE__) . '#', $_SERVER['PHP_SELF'])) { 	die('You are not allowed to call this page directly.'); }/* Edição dos inscritos *//* Esta função é responsável por fazer a alteração dos inscritos*/function ic_update_certificado($minicurso, $quem){	global $wpdb;	if($minicurso[0]->minicurso_certificado == null){ $certificadoNew = $quem; }	else { $certificadoNew = $minicurso[0]->minicurso_certificado."|".$quem;}	$certificadoOK = "update wp_minicursos set minicurso_certificado='".$certificadoNew."' where minicurso_id ='".$minicurso[0]->minicurso_id."' ";	$wpdb->query($certificadoOK);}?>