<?phpif(preg_match('#' . basename(__FILE__) . '#', $_SERVER['PHP_SELF'])) { 	die('You are not allowed to call this page directly.'); }/* Edição dos inscritos *//* Esta função é responsável por fazer a alteração dos inscritos*/function ic_update_inscrito(){	global $wpdb;	$all_nome = split(" ",$_POST['nome-completo']);	$user_nicename = $all_nome[0];	$user_registered=date('d-m-Y'); 	if($_POST['minicurso'] != null) {		foreach($_POST['minicurso'] as $selecionado){			if($minicursos == null) { $minicursos = $selecionado; }			else { $minicursos = $minicursos."|".$selecionado; }		}	}	$recebeMinicursos = split("\|",$_POST['minicursos-old']);	foreach($recebeMinicursos as $recebeMini) {		$sqlMini="select * from wp_minicursos where minicurso_id='".$recebeMini."'";		$inscricaoMinicursos = $wpdb->get_results($sqlMini);		foreach($inscricaoMinicursos as $inscrever) {			$inscritos = split("\|",$inscrever->minicurso_inscritos);			if($inscritos[0] == $_POST['email']){ $inscricaoDel = NULL; }			else { 				if ($inscritos[0] == $_POST['email']) {					$nome = $_POST['email']."|";					$inscricaoDel = str_replace($nome, '', $inscrever->minicurso_inscritos);				}				else {					$nome = "|".$_POST['email'];					$inscricaoDel = str_replace($nome, '', $inscrever->minicurso_inscritos);				}			}			$deletaIncrito = "update wp_minicursos set minicurso_inscritos=\"".$inscricaoDel."\" where minicurso_id ='".$inscrever->minicurso_id."' ";			$wpdb->query($deletaIncrito);			}		}		$recebeMinicursos = split("\|",$minicursos);	foreach($recebeMinicursos as $recebeMini) {		$sqlMini="select * from wp_minicursos where minicurso_id='".$recebeMini."'";		$inscricaoMinicursos = $wpdb->get_results($sqlMini);		foreach($inscricaoMinicursos as $inscrever) {			if($inscrever->minicurso_inscritos == null){ $inscricaoNew = $_POST['email']; }			else { $inscricaoNew = $inscrever->minicurso_inscritos."|".$_POST['email'];}			$atualizaIncritos = "update wp_minicursos set minicurso_inscritos=\"".$inscricaoNew."\" where minicurso_id ='".$inscrever->minicurso_id."' ";			$wpdb->query($atualizaIncritos);			}	}	if($_POST['senha'] != null) {		$senha = md5($_POST['senha']);			$atualizaSenha = "update wp_users set user_pass='".$senha."' where ID='".$_POST['ID']."'"; 		$wpdb->query($atualizaSenha);			}		$sql = "update wp_users set user_nicename=\"".$user_nicename."\", user_registered='".$user_registered."',	display_name=\"".$_POST['nome-completo']."\", instituicao_ensino=\"".$_POST['instituicao-ensino']."\", instituicao_divisao='".$_POST['instituicao-divisao']."', instituicao_vinculo='".$_POST['instituicao-vinculo']."', minicursos='".$minicursos."', certificados=\"".$_POST['certificados']."\" where ID='".$_POST['ID']."'"; 	$wpdb->query( $sql );	include_once ( dirname (__FILE__) . '/ic-inscrito.php' );	ic_inscrito();}function ic_update_inscrito_cert($quem){	global $wpdb;	$sql = "update wp_users set certificados='SIM' where ID='".$quem."'"; 	$wpdb->query( $sql );}?>