<?phpif(preg_match('#' . basename(__FILE__) . '#', $_SERVER['PHP_SELF'])) { 	die('You are not allowed to call this page directly.'); }/* Edição de resumos simples *//* Esta função é responsável por fazer a alteração do resumo simples*/function ic_update_minicurso(){	global $wpdb;	$resumo_dia=date('d-m-Y');	if($_POST['minicurso-id'] != null) { $sql = "update wp_minicursos set minicurso_tema='".$_POST['minicurso-tema']."', minicurso_local='".$_POST['minicurso-local']."', minicurso_professor='".$_POST['minicurso-professor']."', minicurso_data='".$_POST['minicurso-data']."', minicurso_vagas='".$_POST['minicurso-vagas']."', minicurso_carga='".$_POST['minicurso-carga']."', minicurso_horario='".$_POST['minicurso-horario']."' where minicurso_id='".$_POST['minicurso-id']."'"; }	else { $sql = "insert into wp_minicursos set minicurso_tema='".$_POST['minicurso-tema']."', minicurso_local='".$_POST['minicurso-local']."', minicurso_professor='".$_POST['minicurso-professor']."', minicurso_data='".$_POST['minicurso-data']."', minicurso_vagas='".$_POST['minicurso-vagas']."', minicurso_carga='".$_POST['minicurso-carga']."', minicurso_horario='".$_POST['minicurso-horario']."'"; }	$wpdb->query( $sql );	include_once ( dirname (__FILE__) . '/ic-minicurso.php' );	ic_minicurso();}?>