<?phpif(preg_match('#' . basename(__FILE__) . '#', $_SERVER['PHP_SELF'])) { 	die('You are not allowed to call this page directly.'); }/* Edição de resumos simples *//* Esta função é responsável por fazer a alteração do resumo simples*/function ic_update_resumo(){	global $wpdb;	$resumo_dia=date('d-m-Y');	$sql = "update wp_resumos set resumo_instituicao_ensino='".$_POST['instituicao-ensino']."', resumo_instituicao_divisao='".$_POST['instituicao-divisao']."', resumo_area='".$_POST['resumo-area']."', resumo_titulo='".$_POST['resumo-titulo']."', resumo_autores='".$_POST['resumo-autores']."', resumo_texto='".$_POST['resumo-texto']."', resumo_palavras_chaves='".$_POST['resumo-palavras-chaves']."', resumo_forma='".$_POST['resumo-forma']."', resumo_status='publish', resumo_date='".$resumo_dia."' where ID='".$_POST['resumo-inscricao-id']."'";	$wpdb->query( $sql );	include_once ( dirname (__FILE__) . '/ic-resumo.php' );	ic_resumo();}?>